mapper.insert:
INSERT INTO USER (
			id, password
			) values(
			#{id},
			#{password}
		)

mapper.select:
select  count(id)
		from user
		where id = #{id}

select id, password
		from user
		where id = #{id} 
		and password =	#{password}

select TEST_CODE, GUMSA_NAME1 
		from lisc100
		order by TEST_CODE, GUMSA_NAME1;

mapper.insert:
        INSERT INTO lisc501 (SYS_DATE, USER_ID, UPD_DATE, INV_CODE, TEST_CODE)
		        VALUES (
		            CONCAT(
		                DATE_FORMAT(NOW(), '%Y-%m-%d '),
		                CASE WHEN HOUR(NOW()) >= 12 THEN CONCAT('오후 ', IF(HOUR(NOW()) > 12, HOUR(NOW()) - 12, HOUR(NOW()))) ELSE CONCAT('오전 ', HOUR(NOW())) END,
		                DATE_FORMAT(NOW(), ':%i:%s')
		            ),
		            'hict',
		            CONCAT(
		                DATE_FORMAT(NOW(), '%Y-%m-%d '),
		                CASE WHEN HOUR(NOW()) >= 12 THEN CONCAT('오후 ', IF(HOUR(NOW()) > 12, HOUR(NOW()) - 12, HOUR(NOW()))) ELSE CONCAT('오전 ', HOUR(NOW())) END,
		                DATE_FORMAT(NOW(), ':%i:%s')
		            ),
		            #{invCode},
		            #{testCode}
        		);

mapper.delete:
    DELETE FROM lisc501
			    WHERE INV_CODE = #{invCode}
			      AND TEST_CODE = #{testCode}
			    LIMIT 1
